const double M_PI       = 3.141592653589793115997963468544185161590576171875; 
const double SPEED_OF_LIGHT = 299792458;

import "C:/Users/ilyes/OneDrive/Bureau/Ilyes REZGUI/My_Research/DEIN Network Formal Verification/Models/External_c++/libexternal.so" {
    int castToInt(double n);
