        //WD_energy_lost=WD_energy_lost+castToInt(k+y-capacity);
        return capacity; 
    }
    else {
        return k+y;}
}

int discrete_verif_capacity(double x,int y){
double res;
res=verif_capacity(x,y);
return castToInt(res);
}
